mapper.select:

  SELECT *
	FROM 
	    (
	    select rownum rn, b.*
	    from v_buyerpayment b
	    WHERE b_email=#{b_email} AND ROWNUM <=#{pageNum} * #{amount}
	    )

  WHERE rn>(#{pageNum}-1)*#{amount}


 SELECT *
 FROM 
     (
      SELECT rownum rn, b.*
      FROM
        buyer b
      where
	   rownum <= #{pageNum} * #{amount}
	)

where rn > (#{pageNum}-1)*#{amount}

mapper.update:
update buyer
set b_rank = #{b_rank}
where b_email=#{b_email}
