mapper.select:

SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    from report r
    where rownum <=#{pageNum} * #{amount}
    )
WHERE rn > (#{pageNum}-1 )* #{amount}
ORDER BY re_code DESC


SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    FROM report r
    WHERE re_result is null AND rownum <=#{pageNum} * #{amount}
    )
WHERE rn > (#{pageNum}-1 )* #{amount}
ORDER BY re_code DESC

mapper.update:
 	UPDATE report
	SET re_result = 'Y', RE_RPOERTDATE=sysdate

	WHERE re_code= #{re_code}

  	UPDATE REPORT
	SET re_result='D', re_rpoertdate=sysdate,re_denied=#{re_denied}
	WHERE re_code=#{re_code}

mapper.select:
{CALL confirm_report(
   #{p_id, mode=IN, jdbcType=VARCHAR, javaType=STRING},
   #{p_code, mode=IN, jdbcType=VARCHAR, javaType=STRING},
   #{result, mode=OUT, jdbcType=INTEGER, javaType=INTEGER, resultMap=update_newReport}
   )

 }
