mapper.select:

SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    FROM report r
    WHERE
     rownum <=#{pageNum} * #{amount} 




    )
WHERE rn > (#{pageNum}-1 )* #{amount}
ORDER BY re_date DESC


SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    FROM report r
    WHERE re_result is null AND rownum <=#{pageNum} * #{amount}




	 ORDER BY re_code DESC
    )
WHERE rn > (#{pageNum}-1 )* #{amount}

mapper.update:
 	UPDATE report
	SET re_result = 'Y', RE_RPOERTDATE=sysdate

	WHERE re_code= #{re_code}

  	UPDATE REPORT
	SET re_result='D', re_rpoertdate=sysdate,re_denied=#{re_denied}
	WHERE re_code=#{re_code}

mapper.select:

SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    FROM report r
    WHERE re_result ='Y' AND rownum <=#{pageNum} * #{amount}




	 ORDER BY re_code DESC
    )
WHERE rn > (#{pageNum}-1 )* #{amount}


SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    FROM report r
    WHERE re_result ='D' AND rownum <=#{pageNum} * #{amount}




	 ORDER BY re_code DESC
    )
WHERE rn > (#{pageNum}-1 )* #{amount}
