mapper.update:
 	UPDATE report
	SET re_result = 'Y', RE_RPOERTDATE=sysdate

	WHERE re_code= #{re_code}

  	UPDATE REPORT
	SET re_result='D', re_rpoertdate=sysdate,re_denied=#{re_denied}
	WHERE re_code=#{re_code}

mapper.select:

	 SELECT *
	 FROM 
	     (
	     SELECT rownum rn, r.*
	     FROM (SELECT * 
             FROM vw_blacklist
             ORDER BY email) r
	     WHERE rownum <=#{pageNum} * #{amount}





	     )
	 WHERE rn > (#{pageNum}-1 )* #{amount}
