mapper.select:

SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    from report r
    where rownum <=#{pageNum} * #{amount}
    )
WHERE rn > (#{pageNum}-1 )* #{amount}


SELECT *
FROM 
    (
    SELECT rownum rn, r.*
    FROM report r
    WHERE re_result is null AND rownum <=#{pageNum} * #{amount}
    )
WHERE rn > (#{pageNum}-1 )* #{amount}

mapper.update:
 	UPDATE report
	SET re_result = 'Y', RE_RPOERTDATE=sysdate








	WHERE re_code= #{re_code}
